import logging

from handlers.calculator import FSM

logger = logging.getLogger(__name__)


from aiogram import F, types, Router
from aiogram.fsm.context import FSMContext

from filters.chat_types import ChatTypeFilter

from handlers.menu_processing import get_menu_content
from keyboards import reply, inline
from keyboards.inline import MenuCallBack, get_callback_btns

from aiogram.enums import ParseMode

rt_habits = Router()
rt_habits.message.filter(ChatTypeFilter(["private"]))


@rt_habits.callback_query(F.data.startswith('return'))
async def return_to_menu(callback: types.CallbackQuery):
    media, reply_markup = await get_menu_content(level=0, menu_name="main")
    await callback.message.edit_media(media=media, reply_markup=reply.del_kb and reply_markup)
    await callback.answer()


@rt_habits.message(F.text == "–ù–∏–∫–æ—Ç–∏–Ω–æ–≤–∞—è")
async def select_habits(message: types.Message, state: FSMContext):
    await message.answer(
        "1) –ë—Ä–æ—Å–∏—Ç—å –∫—É—Ä–∏—Ç—å –º–≥–Ω–æ–≤–µ–Ω–Ω–æ –ø—Ä–∞–∫—Ç–∏—á–µ—Å–∫–∏ –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ, –ø–æ—ç—Ç–æ–º—É —Å–æ–≤–µ—Ç—É—é –í–∞–º –≤—ã–±—Ä–∞—Ç—å –æ–ø—Ä–µ–¥–µ–ª—ë–Ω–Ω—ã–π <b>–¥–µ–Ω—å X</b> –≤ –∫–∞–ª–µ–Ω–¥–∞—Ä–µ, –∫–æ–≥–¥–∞ –í—ã —Ö–æ—Ç–∏—Ç–µ –æ—Ç–∫–∞–∑–∞—Ç—å—Å—è –æ—Ç —Å–∏–≥–∞—Ä–µ—Ç.\n2) <b>–ü–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ —É–º–µ–Ω—å—à–∞–π—Ç–µ</b> –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —É–ø–æ—Ç—Ä–µ–±–ª—è–µ–º—ã—Ö —Å–∏–≥–∞—Ä–µ—Ç –Ω–∞ –ø—É—Ç–∏ –∫ –¥–Ω—é X.\n3) –ü–µ—Ä–≤–æ–µ –≤—Ä–µ–º—è –ø–æ—Å–ª–µ –¥–Ω—è X <b>—Å–æ—Ö—Ä–∞–Ω—è–π—Ç–µ –ø—Ä–∏–≤—ã—á–∫–∏</b>, –∫–æ—Ç–æ—Ä—ã–µ —Å–æ–ø—Ä–æ–≤–æ–∂–¥–∞–ª–∏ –ø—Ä–æ—Ü–µ—Å—Å –∫—É—Ä–µ–Ω–∏—è. –ù–∞–ø—Ä–∏–º–µ—Ä, –≤—ã—Ö–æ–¥–∏—Ç–µ –Ω–∞ —Å–≤–µ–∂–∏–π –≤–æ–∑–¥—É—Ö –≤–æ –≤—Ä–µ–º—è –ø–µ—Ä–µ—Ä—ã–≤–∞, –Ω–æ –Ω–µ –ø–æ–¥–¥–∞–≤–∞–π—Ç–µ—Å—å —Å–æ–±–ª–∞–∑–Ω—É –ø–æ–∫—É—Ä–∏—Ç—å.\n4) –í—Å—ë, —á—Ç–æ –∞—Å—Å–æ—Ü–∏–∏—Ä—É–µ—Ç—Å—è —Å –∫—É—Ä–µ–Ω–∏–µ–º, –ª—É—á—à–µ <b>—É–±—Ä–∞—Ç—å –∏–∑ –∑–æ–Ω—ã –≤–∏–¥–∏–º–æ—Å—Ç–∏</b>, —á—Ç–æ–±—ã –ª–∏—à–Ω–∏–π —Ä–∞–∑ –Ω–µ –Ω–∞–ø–æ–º–∏–Ω–∞—Ç—å —Å–µ–±–µ –æ —Å–∏–≥–∞—Ä–µ—Ç–∞—Ö.\n5) –ù–∞–π–¥–∏—Ç–µ –∫–æ–≥–æ-—Ç–æ (–≤–æ–∑–º–æ–∂–Ω–æ, —Ü–µ–ª—É—é –∫–æ–º–ø–∞–Ω–∏—é), <b>–∫—Ç–æ –ø–æ–¥–¥–µ—Ä–∂–∏—Ç –≤–∞—à—É –∏–Ω–∏—Ü–∏–∞—Ç–∏–≤—É</b> –∏ —Å–æ—Ö—Ä–∞–Ω—è–π—Ç–µ <b>–ø–æ–∑–∏—Ç–∏–≤</b>, —á—Ç–æ–±—ã –Ω–µ –¥—É–º–∞—Ç—å –æ –∫—É—Ä–µ–Ω–∏–∏.\n6) <b>–ò–∑–º–µ–Ω–∏—Ç–µ —Å–≤–æ–π —Ä–∞—Ü–∏–æ–Ω</b>. –î–æ–±–∞–≤—å—Ç–µ –≤ –Ω–µ–≥–æ –±–æ–ª—å—à–µ –º–æ–ª–æ—á–Ω—ã—Ö –ø—Ä–æ–¥—É–∫—Ç–æ–≤, –∞ —Ç–∞–∫–∂–µ –≤–∏—Ç–∞–º–∏–Ω—ã, –º–∏–Ω–µ—Ä–∞–ª—ã –∏ –∫–ª–µ—Ç—á–∞—Ç–∫—É. –°—Ç–∞—Ä–∞–π—Ç–µ—Å—å –Ω–µ —É–ø–æ—Ç—Ä–µ–±–ª—è—Ç—å ¬´—Ç—è–∂—ë–ª—É—é –ø–∏—â—É¬ª –∏ –ø–µ–π—Ç–µ –±–æ–ª—å—à–µ –≤–æ–¥—ã. \n7)	–ü–æ–ø—Ä–æ–±—É–π—Ç–µ <b>–∑–∞–Ω—è—Ç–∏—è —Å–ø–æ—Ä—Ç–æ–º</b>. –ï—Å–ª–∏ —É –í–∞—Å –µ—Å—Ç—å –∑–∞—Ç—Ä—É–¥–Ω–µ–Ω–∏—è —Å –≤—ã–±–æ—Ä–æ–º —Ç—Ä–µ–Ω–∏—Ä–æ–≤–æ–∫, —è –º–æ–≥—É –ø–æ—Å–æ–≤–µ—Ç–æ–≤–∞—Ç—å –í–∞–º –Ω–µ–∫–æ—Ç–æ—Ä—ã–µ –≤–∞—Ä–∏–∞–Ω—Ç—ã, –∏—Å—Ö–æ–¥—è –∏–∑ –í–∞—à–∏—Ö –ø—Ä–µ–¥–ø–æ—á—Ç–µ–Ω–∏–π.\n8)	–ü–æ–º–æ—á—å –±—Ä–æ—Å–∏—Ç—å –∫—É—Ä–∏—Ç—å —Ç–∞–∫–∂–µ –º–æ–≥—É—Ç <b>–∂–µ–≤–∞—Ç–µ–ª—å–Ω—ã–µ —Ä–µ–∑–∏–Ω–∫–∏</b>, –ø–ª–∞—Å—Ç—ã—Ä–∏ –∏ —Ç–∞–±–ª–µ—Ç–∫–∏, –æ–¥–Ω–∞–∫–æ –ø–µ—Ä–µ–¥ –∏—Ö —É–ø–æ—Ç—Ä–µ–±–ª–µ–Ω–∏–µ–º —Å–æ–≤–µ—Ç—É—é –í–∞–º –ø–æ—Å–æ–≤–µ—Ç–æ–≤–∞—Ç—å—Å—è —Å –≤—Ä–∞—á–æ–º.\n9)	–ö–∞–∫ —É—Ç–≤–µ—Ä–∂–¥–∞—é—Ç —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç—ã, –≤ —Å—Ä–µ–¥–Ω–µ–º, —Ç—è–≥–∞ –∫ –∫—É—Ä–µ–Ω–∏—é –ø—Ä–æ—Ö–æ–¥–∏—Ç —á–µ—Ä–µ–∑ 15-20 –¥–Ω–µ–π, –ø–æ—ç—Ç–æ–º—É <b>–≤—Å—ë –≤ –í–∞—à–∏—Ö —Ä—É–∫–∞—Ö!</b>",
        parse_mode=ParseMode.HTML, reply_markup=reply.del_kb)

    await message.answer_photo(
        photo="https://media1.thehungryjpeg.com/thumbs2/ori_4028488_mubyalpxa3p46fynmhtf2tv0mycfydihik3339el_neon-arrow-realistic-glowing-yellow-sign-abstract-electricity-colore.jpg",
        reply_markup=get_callback_btns(btns={'–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é': 'return'})
    )
    await state.set_state(FSM.main__menu)


@rt_habits.message(FSM.addiction, F.text == "–ê–ª–∫–æ–≥–æ–ª—å–Ω–∞—è")
async def select_habits(message: types.Message, state: FSMContext):
    await message.answer(
        "1) –û–¥–Ω–æ–π –∏–∑ –≥–ª–∞–≤–Ω—ã—Ö –æ—à–∏–±–æ–∫ –ø—Ä–∏ –±–æ—Ä—å–±–µ —Å –∞–ª–∫–æ–≥–æ–ª–∏–∑–º–æ–º —è–≤–ª—è–µ—Ç—Å—è –ø–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ–µ ¬´–ø–æ–Ω–∏–∂–µ–Ω–∏–µ –≥—Ä–∞–¥—É—Å–∞¬ª. –í –¥–∞–Ω–Ω–æ–º —Å–ª—É—á–∞–µ —ç—Ç–æ –Ω–µ –ø–æ–º–æ–∂–µ—Ç, –ø–æ—ç—Ç–æ–º—É —Å–æ–≤–µ—Ç—É—é –í–∞–º <b>–æ—Ç–∫–∞–∑–∞—Ç—å—Å—è –æ—Ç –≤—Å–µ–≥–æ –∞–ª–∫–æ–≥–æ–ª—å–Ω–æ–≥–æ –Ω–∞—Å–æ–≤—Å–µ–º</b>, –µ—Å–ª–∏ –í—ã —Ä–µ—à–∏–ª–∏ –±—Ä–æ—Å–∏—Ç—å –ø–∏—Ç—å.\n2)	–ü–æ –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏ –æ—Ç–∫–∞–∂–∏—Ç–µ—Å—å –æ—Ç –ø–æ—Å–µ—â–µ–Ω–∏—è –º–µ—Å—Ç, –≥–¥–µ –±—É–¥–µ—Ç —Å–ø–∏—Ä—Ç–Ω–æ–µ (–∫–ª—É–±—ã, –±–∞—Ä—ã –∏ –¥—Ä—É–≥–∏–µ —Ç–æ—Ä–∂–µ—Å—Ç–≤–∞). –ï—Å–ª–∏ –∂–µ –æ—Ç–∫–∞–∑ –Ω–µ–≤–æ–∑–º–æ–∂–µ–Ω, –≤–æ –≤—Ä–µ–º—è –º–µ—Ä–æ–ø—Ä–∏—è—Ç–∏—è –∞–ª–∫–æ–≥–æ–ª—é –Ω—É–∂–Ω–æ <b>–Ω–∞–π—Ç–∏ –∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤—É</b> (–ø–æ–¥–æ–π–¥—É—Ç, –Ω–∞–ø—Ä–∏–º–µ—Ä, —Å–æ–∫ –∏–ª–∏ –æ–±—ã—á–Ω–∞—è –≤–æ–¥–∞).\n3)	–ü—Ä–µ–¥—É–ø—Ä–µ–¥–∏—Ç–µ –≤—Å–µ—Ö —Å–≤–æ–∏—Ö –±–ª–∏–∑–∫–∏—Ö –∏ –∑–Ω–∞–∫–æ–º—ã—Ö –æ –≤–∞—à–µ–π —Ü–µ–ª–∏. –ü–æ–ø—Ä–æ—Å–∏—Ç–µ –∏—Ö –ª–∏—à–Ω–∏–π —Ä–∞–∑ –Ω–µ –Ω–∞–ø–æ–º–∏–Ω–∞—Ç—å –í–∞–º –æ–± –∞–ª–∫–æ–≥–æ–ª–µ. \n4)	–í—Å–µ <b>–∏–º–µ—é—â–∏–µ—Å—è —É –í–∞—Å –¥–æ–º–∞ –∞–ª–∫–æ–≥–æ–ª—å–Ω—ã–µ –Ω–∞–ø–∏—Ç–∫–∏</b> —Å–æ–≤–µ—Ç—É–µ–º –æ—Ç–¥–∞—Ç—å –í–∞—à–∏–º –∑–Ω–∞–∫–æ–º—ã–º –∏–ª–∏ –ø—Ä–æ—Å—Ç–æ <b>–≤—ã–±—Ä–æ—Å–∏—Ç—å</b>.\n5)	–ü–µ–π—Ç–µ <b>–±–æ–ª—å—à–µ –≤–æ–¥—ã</b> –∏ —Å–æ–∫—Ä–∞—Ç–∏—Ç–µ —É–ø–æ—Ç—Ä–µ–±–ª–µ–Ω–∏–µ –∫–æ—Ñ–µ –∏ —á–∞—è. –î–æ–±–∞–≤—å—Ç–µ –≤ —Ä–∞—Ü–∏–æ–Ω <b>–±–æ–ª—å—à–µ —Ñ—Ä—É–∫—Ç–æ–≤ –∏ –æ–≤–æ—â–µ–π</b> –∏ –∏–∑–±–µ–≥–∞–π—Ç–µ —Å–æ–ª—ë–Ω—É—é –∏ –æ—Å—Ç—Ä—É—é –ø–∏—â—É.\n6)	–î–ª—è –ø–æ–¥–¥–µ—Ä–∂–∞–Ω–∏—è —ç–º–æ—Ü–∏–æ–Ω–∞–ª—å–Ω–æ–≥–æ —Ñ–æ–Ω–∞ –Ω–∞–π–¥–∏—Ç–µ –¥–ª—è —Å–µ–±—è <b>–Ω–æ–≤–æ–µ —Ö–æ–±–±–∏</b>. –ù–∞—á–Ω–∏—Ç–µ –±–æ–ª—å—à–µ –≤—Ä–µ–º–µ–Ω–∏ –ø—Ä–æ–≤–æ–¥–∏—Ç—å –Ω–∞ —Å–≤–µ–∂–µ–º –≤–æ–∑–¥—É—Ö–µ –∏ –∑–∞–Ω–∏–º–∞—Ç—å—Å—è —Å–ø–æ—Ä—Ç–æ–º. –ï—Å–ª–∏ —É –í–∞—Å –µ—Å—Ç—å –∑–∞—Ç—Ä—É–¥–Ω–µ–Ω–∏—è —Å –≤—ã–±–æ—Ä–æ–º —Ç—Ä–µ–Ω–∏—Ä–æ–≤–æ–∫, —è –º–æ–≥—É –ø–æ—Å–æ–≤–µ—Ç–æ–≤–∞—Ç—å –í–∞–º –Ω–µ–∫–æ—Ç–æ—Ä—ã–µ –≤–∞—Ä–∏–∞–Ω—Ç—ã, –∏—Å—Ö–æ–¥—è –∏–∑ –í–∞—à–∏—Ö –ø—Ä–µ–¥–ø–æ—á—Ç–µ–Ω–∏–π.üòâ\n7)	–ü–æ–º–Ω–∏—Ç–µ, —á—Ç–æ —Ä–µ–∑–∫–∏–π –æ—Ç–∫–∞–∑ –æ—Ç —Å–ø–∏—Ä—Ç–Ω–æ–≥–æ –º–æ–∂–µ—Ç –ø—Ä–∏–≤–µ—Å—Ç–∏ –∫ —Å–±–æ—é –ø—Ä–∏–≤—ã—á–Ω–æ–≥–æ —Ä–∏—Ç–º–∞ –æ—Ä–≥–∞–Ω–∏–∑–º–∞, —á—Ç–æ –ø–æ–≤–ª–µ—á—ë—Ç –∑–∞ —Å–æ–±–æ–π –ø–ª–æ—Ö–æ–µ —Å–∞–º–æ—á—É–≤—Å—Ç–≤–∏–µ, –ø–µ—Ä–µ–ø–∞–¥—ã –≤ –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–∏ –∏ –¥—Ä—É–≥–∏–µ –Ω–µ–≥–∞—Ç–∏–≤–Ω—ã–µ –ø–æ—Å–ª–µ–¥—Å—Ç–≤–∏—è. –ï—Å–ª–∏ –í—ã –Ω–µ —É–≤–µ—Ä–µ–Ω—ã –≤ —Å–≤–æ–∏—Ö —Å–æ–±—Å—Ç–≤–µ–Ω–Ω—ã—Ö —Å–∏–ª–∞—Ö, —Å–æ–≤–µ—Ç—É—é <b>–æ–±—Ä–∞—Ç–∏—Ç—å—Å—è –∫ –≤—Ä–∞—á—É-—Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç—É</b>. –û–Ω –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ –ø–æ–º–æ–∂–µ—Ç –∏ –ø–æ–¥–±–µ—Ä—ë—Ç –¥–ª—è –í–∞—Å –∏–Ω–¥–∏–≤–∏–¥—É–∞–ª—å–Ω—ã–π –ø–ª–∞–Ω –ø–æ –æ—Ç–∫–∞–∑—É –æ—Ç –∞–ª–∫–æ–≥–æ–ª—è.",
        parse_mode=ParseMode.HTML, reply_markup=reply.del_kb)

    await message.answer_photo(
        photo="https://media1.thehungryjpeg.com/thumbs2/ori_4028488_mubyalpxa3p46fynmhtf2tv0mycfydihik3339el_neon-arrow-realistic-glowing-yellow-sign-abstract-electricity-colore.jpg",
        reply_markup=get_callback_btns(btns={'–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é': 'return'})
    )
    await state.set_state(FSM.main__menu)
